#ifdef GL_ES
precision mediump float;
#endif

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalizza le coordinate
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Impostazioni per la pallina al plasma
    float radius = 0.3 + 0.1 * sin(iTime * 2.0); // Raggio della pallina che cambia nel tempo
    float plasmaFactor = 0.5 + 0.5 * sin(iTime * 3.0); // Fattore per il plasma
    float distortion = sin(iTime * 0.3); // Distorsione per l'effetto plasma
    float pulse = texture(iChannel0, vec2(iTime * 0.1, 0.0)).r * 0.5; // Frequenze basse (modifica del raggio)

    // Calcola la distanza dal centro della pallina
    float dist = length(uv);

    // Applica una distorsione per ottenere l'effetto al plasma
    float effect = sin(dist * 10.0 - iTime * 2.0 + pulse * 5.0);
    effect *= exp(-dist * 3.0); // Riduci l'intensit√† lontano dal centro

    // Creazione della pallina con distorsione
    if (dist < radius + effect) {
        fragColor = vec4(0.5 + 0.5 * sin(iTime * 5.0 + effect * 3.0), 
                         0.5 + 0.5 * cos(iTime * 5.0 + effect * 2.0), 
                         0.5 + 0.5 * sin(iTime * 5.0 + effect), 
                         1.0);
    } else {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0); // Sfondo nero
    }
}
