#define R3D(a) mix(p*q*dot(p,p*q), p, cos(a)) + sin(a)*cross(p,p*q)
#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(-1,0,1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e)*1.5;
    p.z-=2.;
    p.yz*=R2D(18.);
    p.z+=tan(iTime);

    p=abs(abs(R3D(4.18)));
    d=cos(ceil(length(p.xy)-.3));
    d-=dot(cos(p),sin(q))/2e4;
    e+=d*d;

}o+=vec4(sqrt(p*p),1)*3./i;
}
