void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 resolution = iResolution.xy;
    vec2 resolution1 = fragCoord/iResolution.xy;
    vec3 backgroundColor = 0.5*cos(iTime+resolution1.xyx+vec3(3,0,4));
    fragColor = vec4(backgroundColor, 1.0);

    vec2 circle1 = vec2(0.5 + cos(iTime*5.0)*0.5, 0.5); 
    vec2 circle2 = vec2(0.2, 0.5 + 0.2 * sin(iTime/2.0)); 

    vec3 color1 = vec3(circle1.x, circle1.y, 1.); 
    vec3 color2 = vec3(0.0, 0.0, 0.1);
    vec3 color3 = vec3(0.1, 0.8, sin(iTime+5.0)); 

    float distance1 = length(fragCoord - resolution * circle1);  
    float distance2 = length(fragCoord - resolution * circle2);

    float circleRadius1 = 0.08 * resolution.x;
    float circleRadius2 = 0.2 * resolution.x;

    vec3 finalColor = backgroundColor;

    if (distance2 < circleRadius2) {
        finalColor = color2;
    }

    if (distance1 < circleRadius1) {
        finalColor = color1;
    }

    if (resolution1.x <= resolution1.y && resolution1.x >= 0.5 && resolution1.y >= 0.2 && resolution1.y <= 0.7) finalColor = color3;

    fragColor = vec4(finalColor, 4.0);

}
