mat2 rot2D(float angle){
    float s = sin(angle);
    float c = cos(angle);
    return mat2(c, -s, s, c);
}

float sdOctahedron(vec3 p, float s) {
    p = abs(p);
    return (p.x+p.y+p.z-s)*0.57735027;
}

vec3 palette(float t) {
    return .5+.5*cos(9.28318*(t+vec3(.4,.516,.557)));
}
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float smin(float a, float b, float k) {
  float h = clamp(0.5 + 0.5*(a-b)/k, 0.0, 1.0);
  return mix(a, b, h) - k*h*(1.0-h);
}

float map(vec3 pos){
    vec3 spherePos = vec3(0,0,0);
    float sphere = length(pos - spherePos) - 1.56;
    vec3 q = pos;
    q.z -= iTime;
    q = fract(q)-0.5;
    float rat = 1.5;
    q.z = mod(q.z, rat) - rat/2.;
    q.xy*=rot2D(iTime);
    float box = sdBox(q*10., vec3(1.))/10.;
    //return box;
    //return smin(box, sphere, 0.5);
    return smin(smin(sphere, pos.y + 0.7, 0.5), box, 0.5);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 m = (iMouse.xy * 2. - iResolution.xy)/iResolution.y;

    m += vec2(1.75, 3.);

    vec2 uv = (fragCoord * 2. - iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0, 0, -6);
    vec3 rd = normalize(vec3(uv.xy,1));
    float travel = 0.;

    ro.yz *= rot2D(m.y);
    rd.yz *= rot2D(m.y);

    ro.xz *= rot2D(m.x);
    rd.xz *= rot2D(m.x);

    vec3 col = vec3(0.0);
    for(int i = 0; i < 100; i = i + 1){
        vec3 pos = ro + rd*travel;
        pos.yz *= rot2D(travel*0.2);
        rd.yz += 0.005;
        float dist = map(pos);
        travel = travel + dist;
        col = vec3(i)/100.;
        if(dist < 0.002 || dist > 1000.) {break;}
    }

    travel = travel*0.1;
    fragColor = vec4(palette(travel),1.0);
    fragColor *= vec4(palette(col.x),1.0);
}
