float Circle(vec2 uv, vec2 p, float r, float blur) 
{
    float d = length(uv - p);    

    float c = smoothstep(r, r - blur, d);

    return c;
}

float Face(vec2 uv, vec2 p, float size)
{
    uv -= p;    //moving coordinate
    uv /= size; //Scaling the coordinate

    float face = Circle(uv, vec2(0, 0), .42, .01);
    face -= Circle(uv, vec2(-.2, .15), .1, .01); //eye
    face -= Circle(uv, vec2(.3, .15), .1, .01);   //eye

    float mouth = Circle(uv, vec2(.02, -.15), .21, .01); //mouth
    face -= mouth;

    return face;
}

float Band(float t, float start, float end, float blur)
{
    float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);

    return step2*step1;
}

float Rect(vec2 uv, float xOffset, float left, float right, 
                    float yOffset, float bot, float top, float blur)
{
    float band1 = Band(uv.x + xOffset, left, right, blur);
    float band2 = Band(uv.y+ yOffset, bot, top, blur);

    return band2*band1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(0.);

    float mask = 0.;
    //Face(uv, vec2(0., .2), 2.);

    mask = Rect(uv, -.3, -.1, .1, 0., -.2, .2, .01);
    mask += Rect(uv, .3, -.1, .1, -.1, -.2, .2, .01);



    //basically mix is lerp, using main color > into what color u want > the mask
    col = mix(col.rgb, vec3(1.,1.,1.), mask);



    fragColor = vec4(vec3(col), 1);
}
