void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    // Rotation
    float rotation = iTime * .2; // Adjust rotation speed here
    float s = sin(rotation);
    float c = cos(rotation);
    mat2 rotationMatrix = mat2(c, s, s, -c);
    uv = rotationMatrix * uv;

    // Time-based scaling
    float scale = 0.01 + 0.05 * sin(0.05*iTime);
    uv /= scale;

    // Create some oscillating patterns
    float pattern1 = sin(20.0 * length(uv) + iTime);
    float pattern2 = sin(5.0 * (uv.x + uv.y) + 0.1*iTime);

    // Combine patterns
    vec3 color = vec3(pattern1 * pattern2);

    // Output to screen
    fragColor = vec4(color, 1.0);
}
