#define R3D(a,A) mix(A*dot(p,A), p, cos(a)) + sin(a)*cross(p,A)
#define R2D(a)       mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float a,i,j,d=.1,e,n; vec3 p,q=vec3(-2,2,1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e)*2.;
    p.z-=2.;
    p.yz*=R2D(iTime*.5);
    p=R3D(.577,q);

    for(j=0.;j++<16.;){
        p=1.-abs(p)-.5;
        a=atan(p.x,p.y)*7.85;
        n=1.04;//PI/3.;
        d=cos(floor(.5+a/n)*n-a)*length(p)-.5;
    }
    e+=d;

}o+=vec4(sqrt(p),1)*3./i;
}
