#define NUM_PARTICLES 250

float rand(vec2 co) {
    return fract(sin(dot(co.xy, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    // Particle properties
    vec2 particlePos[NUM_PARTICLES];

    // Initialize particle positions randomly
    for (int i = 0; i < NUM_PARTICLES; i++) {
        particlePos[i] = vec2(rand(vec2(i, 0.0)), rand(vec2(i, 1.0)));
        float depth = rand(vec2(i*i, 0.5));
        particlePos[i].x += sin(iTime/1.0) / 8.0 * depth;
        particlePos[i].y += cos(iTime/2.0) / 4.0 * depth;
        //particlePos[i].x = (particlePos[i].x + iTime/100.0);
    }

    // Calculate distance to the particles
    vec3 color = vec3(1.0);  // White color

    for (int i = 0; i < NUM_PARTICLES; i++) {
        float depth = rand(vec2(i*i, 0.5));
        float particleSize = 0.05 * depth;
        float distance = length(uv - particlePos[i]);
        float step = smoothstep(particleSize, 0.0, particleSize - distance);
        color.r *= step * 1.0;
        color.g *= step * 1.0;
        color.b *= step * 0.0;
    }

    //color.r = smoothstep(0.0, 0.2, color.r);
    color.g = smoothstep(0.2, 0.6, color.r);

    fragColor = vec4(vec3(1.0) - color, 1.0);
}
