void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Fake anaglyph
    vec4 left = texture(iChannel0, uv - vec2(0.025, 0));
    vec4 right = texture(iChannel0, uv + vec2(0.025, 0));

    // Grayscale the right and use it as the blue channel
    float rightShade = (right.r + right.g + right.b) / 3.0f;
    fragColor = vec4(left.r, left.g, rightShade, left.a);
}
