// Fork of "better template" by ProfessorPixels. https://shadertoy.com/view/l3yGRV
// 2024-08-01 23:05:36
#define P vec3(cos(iTime)*p.x-sin(iTime)*p.z,p.y,sin(iTime)*p.x+cos(iTime)*p.z)
#define S (length(P)-2.-smoothstep(0.,1.,texture(iChannel0,P.xy*.1).r)*.1)
void mainImage(out vec4 O,vec2 F)
{
    vec3 p=vec3(0,0,3),t=normalize(vec3(F-iResolution.xy*.5,-100));
    for (float d; d<400.;d+=1.)
    {
        p+=t*S;
        if (S<.001) break;
    }
    if (S<.001)
    {
        O=.7+.3*vec4(dot(normalize(p),normalize(vec3(1))));
        O*=mix(vec4(0,.8,1,0),vec4(0,1,.2,0),step(.6,texture(iChannel0,P.xy*.1).r));
    }
    else O=pow(texture(iChannel1,2.*F/iResolution.xy).gggg,vec4(8.));
}
