#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
void mainImage(out vec4 o, vec2 u) {    
    float t = iTime, x, i, j, d = 1., 
          s, e, f, g;

    vec3  r = iResolution, q, k, 
          D = normalize(vec3(u + u - r.xy, 4. * r.y)),
          p = vec3(0, 0, -10. + cos(.3 * t) * .3 );

    f = cos(t * .8) * .5 - 2.;

    o = vec4(1);
    while(i++ < 1e2 && d > 4e-3) {
        q = p;
        q.zx *= rot(cos(.3 * t) * .4);
        q.zy *= rot(sin(.2 * t) * .4);
        q.xy *= rot(.2 * t);

            e = 0.;
            d = (length(q*1.) -  3.*1.) *.02;
            for(j = 1.; j < 129.; j += j)
                k = sin(3. * q * j),
                e += abs((k.x + k.y+ k.z) / j);

            s = (length(p + vec3(cos(t * .3) * 12.,0,0)) -  4.5) *.04;
            if(s > 0.){
                d = min((d + .01 * f * e), s + .002 * f * sqrt(e));
                if(s < .01) o.bg += .005;
            }
            else d = min((d + .01 * f * e), s + .1 * f * sqrt(e));

        s = d;
        d = abs(d) - .0006;
        d = max(d, -p.z - 5.);
        e = .0009 + .0001 * cos(t * .7);
        g = .002;
        if(s < g) {
            if(s < -.01) o -= vec4(4, 3, 10, 0) / 3e2;
            else o -= vec4(3, 4, 6, 0) / 3e2;
        }
        else if(s < g + 2. * e) o.rb -= .03;
        else if(s < g + 4. * e) o.gb -= .02;
        else if(s < g + 3. * e) o.rg -= .03;
        else o -= vec4(.006);
        p += d * D;
    }
    o *= o * o;
}
