void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float time = iTime;

    float frequency = 4.0;
    float pause = 0.0;
    float num = 1.0;

    float repeatTime = mod(time, pause + frequency);
    float timeStep = step(repeatTime,frequency);

    float uvOffset = uv.y - (timeStep * repeatTime)*2.0;

    float pattern = fract(uvOffset*num);

    float mask = timeStep * step(0.5, fract(uvOffset/2.0));

    float texOffset = pattern*mask;

    // Output to screen
    fragColor = vec4(texOffset,0.0,0.0,1.0);
}
