vec4 textureEffect(in vec2 fragCoord, float iTime)
{
    vec2 uv = fragCoord / iResolution.xy;
    uv *= 1.2 + sin(iTime * -0.2) * 0.09;
    uv += sin(uv * -4.0 + iTime) * 0.31;

    // Wave Distortion
    float wave = sin(uv.y * 10.0 + iTime) * 0.05;
    uv.x += wave;

    // Zoom Effect
    float zoom = 0.4 + sin(iTime * -0.1) * 0.1;
    uv = uv * zoom + 0.5 * (1.0 - zoom);

    return texture(iChannel0, uv);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    fragColor = textureEffect(fragCoord, iTime);
}
