#define cor(a) (cos(a * 6.3 + vec4(0, 23, 21, 0)) * .5 + .5)
#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define t iTime
#define torus(q)                       \
    length(vec2(length(q.xy)           \
        - 1. - cos(atan(q.x, q.y) * 5.) * .1,    \
        q.z)) - .09

float map(vec3 p) {
    p.yz *= rot(cos(t * .3) * 1.);
    p.xy *= rot(cos(t * .2) * 3.);

    vec3 q;
    float i, d, s = 1.;

    while(i++ < 5.)
        q = p,
        q.xy *= rot(i * 6.28/5.),
        q.xz *= rot(6.28/6.),
        d = torus(q),
        d = i < 4.? abs(d): d,
        s = min(s, d);

    return s;
}


void mainImage(out vec4 o, vec2 u) {
    float d, layers, s, i, b, mx, my,
          t = iTime;

    vec2 R = iResolution.xy;
         u = (u + u - R) / R.y;

    vec3 rd = normalize(vec3(.2 * u, dot(u, u) * 1.2)), p;
    o *= 0.;

    rd.yz *= rot(cos(t * .3) * .3);
    rd.xy *= rot(cos(t * .2) * 3.);


    while(i++ < 24.) {
        s = map(p);

        d += max(s, 2e-3);

        p = d * rd;
        p.z += -2.5; 

        s < .004 
            ? o += .004 / d * i 
                + cor(i / 20.) / 160.
            : o;
    }
}
