# define PI 3.141592

float gyroid( vec3 v ) {
    return dot(sin(v), cos(v.yzx));
}

float wave( vec2 v, float t ) {
    return gyroid(vec3(3. * v, length(12. * v) - t));
}

void mainImage( out vec4 col, in vec2 coord ) {
    vec2 r = iResolution.xy,
         v = (2. * coord - r) / min(r.x, r.y);

    float t = iTime,
          i, d, s, a;

    for (i; i < 6.; i++) {
        d = mod(i, 2.) * 2. - 1.;
        s += wave(v - vec2(cos(i), sin(i)), d * t);
    }

    a = 1.1 * cos(1620. * exp(.0001 * s * s));

    col.xyz = a * a * a * a * (.5 + .5 * cos(t + v.xyx + vec3(0, 2, 4)));
}
