#define PI 3.14596723f

vec2 rotate(vec2 vector, float theta, vec2 about)
{
    vector -= about;
    float sin_theta = sin(theta);
    float cos_theta = cos(theta);
    vector = vec2(vector.x * cos_theta - vector.y * sin_theta, vector.x * sin_theta + vector.y  * cos_theta);
    vector += about;
    return vector;
}

float norm_cos(float theta)
{
    return (cos(theta) + 1.0f) / 2.0f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv = rotate(uv,iTime * 0.75f, vec2(0.5f, 0.5f));

    vec4 text_col = texture(iChannel0, uv);

    fragColor = vec4(text_col);
}
