//https://www.shadertoy.com/view/4djSRW
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}
#define R iResolution.xy
#define ss(x) smoothstep(.0,.01,x)

vec2 P(vec2 c){ return hash22(c)-.5;}

vec3 VoronoiD(vec2 U){

    vec2 C =floor(U+.5);       

    vec3 M = vec3(C,1e10);
    for(float x=-2.;x<=2.;x++)
    for(float y=-2.;y<=2.;y++){
        vec2 N= C+vec2(x,y),
        H=P(N)+N, D=abs(H-U);

        vec2 T= normalize(
            iMouse.z>0.? 
            (iMouse.xy-R*.5)/R.y*6.-U:
            vec2(cos(iTime*.7),sin(iTime))*2.5*R/R.y-U
        );

        float d = length(U-H)* (.5 + .4*dot(normalize(H-U),T));

        if(d < M.z ){
            M.z=d;
            M.xy=H;           
        }
    }
    return M;

}

void mainImage( out vec4 O, in vec2 I )
{

    vec2 U = (I - R*.5) /R.y*6. ;
    vec3 col = vec3(0);

    vec2 C =floor(U+.5);       

    vec3 M = VoronoiD(U) ;

    col= (0.5 + 0.5*cos(M.xyx +vec3(4,2,0)))*(1.-M.z*2.);
    if(iMouse.z>0.) col =mix(vec3(1),col,ss(M.z-.01));

    O = vec4(col,1.0);
}
