// Converted from some stuff I'm doing on sh8r.org.
// None of this makes any sense and its missing the zoom/pan controls.
// Derivative works are welcome, show me those wacky fractys.
void mainImage( out vec4 o, in vec2 F )
{
    #define P(v, k) pow(v, k)
    float esc = 6e1, at = .25, sc = .2, it = 40.;
    vec3 hue = vec3(0, .5, 1);
    float B, I,
        e = esc, le = log2(e), k = 1./sc;
    vec2 z, c = (F+F-iResolution.xy)/iResolution.y;
    c = vec2(c.x-.5, c.y);
    int i, h = int(it);
    for (; i < h && B < e;
        // I'm just throwing stuff at z.           .2 = intensity?
        z = vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y)*exp(.2*cos(atan(z)*log2(1.+I) + iTime)) + c,
        I = float(++i),
        // Also throw stuff at the boundary.
        B = z.x*z.x+4.*le*z.y*cos(atan(z.y, z.x) + I + iTime) 
    ); o = i < h?
        vec4((.5+.5*cos(at*cos(atan(z.y, z.x) + I + iTime)
            + I*sc + iTime + 2.*(hue)))
        *smoothstep(1., 0., P(I, k)/P(float(h), k))
        *smoothstep(-le, le, log2(B/e)), 1)
    : vec4(0,0,0,1);
}
