void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy; // Normalize coordinates
    uv = uv * 2.0 - 1.0;                    // Transform to range [-1, 1]
    uv.x *= iResolution.x / iResolution.y;  // Correct aspect ratio

    float angle = atan(uv.y, uv.x);         // Calculate angle
    float radius = length(uv);             // Calculate radius

    // Add time to the angle for counterclockwise rotation
    angle += iTime;

    // Create spiral pattern
    float spiral = sin(angle * 10.0 + radius * 20.0);

    // Map the spiral value to colors
    vec3 color = 0.5 + 0.5 * cos(spiral + vec3(0.0, 2.0, 4.0));

    fragColor = vec4(color, 1.0);           // Output color
}
