void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float size = 40.0;
    vec2 sFrag = fragCoord / size;
    vec2 tile = floor(sFrag);
    vec2 cdisp = sFrag - (tile + vec2(0.5));
    float period = 1.5;
    float r = max(0.0, 1.7 * sqrt(2.0) * (abs(mod(iTime, period) / period - 0.5)));
    float len = length(cdisp);
    float c = (len - r + 0.25) * 2.0 + 0.5;
    fragColor =  vec4(c);
}
