void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalize ekran koordinatları
    vec2 uv = fragCoord / iResolution.xy;
    uv -= 0.5; // Merkezi (0,0) yap
    uv.x *= iResolution.x / iResolution.y; // En-boy oranını düzelt

    // Zaman değişkeni
    float time = iTime;

    // Büyük Çokgen Parametreleri
    float sides = 6.0; // Sabit kenar sayısı
    float radius = 0.4 + 0.05 * sin(time * 3.0); // Hafifçe değişen yarıçap
    float angleOffset = time * 0.8; // Daha hızlı dönen çokgen (saat yönü)

    // Büyük Çokgen Hesaplaması
    float angle = atan(uv.y, uv.x) + angleOffset;
    float len = length(uv);
    float segmentAngle = 2.0 * 3.141592 / sides;
    float edge = cos(floor(0.5 + angle / segmentAngle) * segmentAngle - angle) * radius;

    // Büyük Çokgen Şekli
    float bigShape = step(len, edge);

    // Merkezde Dönen Parlak Dörtgen
    float smallSides = 4.0; // Dörtgen
    float smallRadius = 0.2 + 0.03 * cos(time * 4.0); // Hafif dalgalı dörtgen yarıçapı
    float smallAngleOffset = -time * 1.2; // Daha hızlı ters dönen dörtgen (saat yönünün tersi)
    float smallSegmentAngle = 2.0 * 3.141592 / smallSides;
    float smallEdge = cos(floor(0.5 + angle / smallSegmentAngle) * smallSegmentAngle - angle + smallAngleOffset) * smallRadius;
    float smallShape = step(len, smallEdge);

    // Renkler
    vec3 bigColor = vec3(0.3, 0.6, 0.9) * bigShape; // Büyük çokgen rengi
    vec3 smallColor = vec3(1.0, 0.7, 0.3) * smallShape; // Merkez dörtgen rengi (parlak)

    // Final Renk Karışımı
    vec3 finalColor = bigColor + smallColor;

    // Hafif Parlaklık Artışı
    finalColor *= 0.8 + 0.2 * sin(time * 5.0);

    // Çıkış Rengi
    fragColor = vec4(finalColor, 1.0);
}
