void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // grid size is NxN squares, aka 4 = 16 squares
    float sqrs = 4.0;


    float barX = step(0.5, mod(uv.x * (sqrs*0.5), 1.0));

    barX *= step(0.5, mod(uv.y * (sqrs*0.5), 1.0));

    float barY = step(0.5, mod(uv.x * (sqrs*0.5) + 0.5, 1.0));

    barY *= step(0.5, mod(uv.y * (sqrs*0.5) + 0.5, 1.0));

    float strength = barX + barY;

    fragColor = vec4(strength, strength, strength, 1.0);
}
