void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy - .5;


    uv = 2.* ( fragCoord - .5*iResolution.xy )/iResolution.y;  

    //uv.y += sin(iTime);
    //uv.x += iTime;
    float a = iTime;
    mat2 rotation =mat2(cos(a),-sin(a),sin(a),cos(a));;

    vec3 col;
    float d = 2.;
    for(float theta = 0.; theta < radians(360.);theta +=radians(10.)){
        for(float phi = 0.; phi < radians(180.); phi += radians(10.)){

            vec3 z = vec3(sin(theta)* sin(phi),cos(theta) * sin(phi),cos(phi));

            //uv = mod(uv,2.)-1.;

            z.xz *= rotation;
            z.yz *= rotation;

            z.xy = vec2(z.x/(d - z.z),z.y/(d-z.z));
            if(length(z.xy - uv) - .01 < 0.){
            col = vec3(length(z));
            break;//exit loop
            }

        }
    }

    fragColor = vec4(col,1.0);
}
