void mainImage(out vec4 color, in vec2 coord) {

    vec2 uv = coord / iResolution.xy;
    color = texture(iChannel0, uv); 


}

void mainImage( out vec4 fragColor, in vec2 uv )
{
    float b = floor(float(iFrame));
    if(mod(b,1.)<=0.){

    uv.x = 10.*(uv.x-0.5*iResolution.x)/iResolution.y;
    uv.y = 10.*(uv.y-0.5*iResolution.y)/iResolution.y;
    vec3 col = vec3(0.);


    float a=0.35*(1.-1.*cos(float(iFrame)/(1.*200.)));



    for(float n=0.5; n<=1.; n+=0.001){

    float l = 0.3+0.05*pow(2.,length(uv));
    float bx=0.5/(a+pow(sin(n*(uv.x*l)),2.));
    float by=0.5/(a+pow(sin(n*(uv.y*l)),2.));

    float val=4.*pow(2.,-1.*(bx+by));

    float v1=pow(2.,-20.*abs(val-1.));
    float v2=pow(2.,-20.*abs(val-1.5));
    float v3=pow(2.,-20.*abs(val-2.));
    col+=0.025*vec3(v1,v2,v3);
    }

    fragColor = vec4(col,1.0);
    }else{
    uv = uv / iResolution.xy;
    fragColor = texture(iChannel0, uv);    
    }
}
