void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float tileSize = abs(sin(iTime/2.0))*100.0 + 10.0;

    vec2 pos = floor(fragCoord/tileSize);

    float b = 0.0;
    if (mod(pos.x+pos.y, 2.0) == 1.0) {
        b = 0.0;
    } else {
        b = 1.0;
    }

    vec3 c = vec3(b, b, b);
    fragColor = vec4(c, 1.0);
}
