vec3 blue = vec3(1.0 / 255.0, 33.0 / 255.0, 105.0 / 255.0);
vec3 white = vec3(1.0, 1.0, 1.0);
vec3 red = vec3(200.0 / 255.0, 16.0 / 255.0, 46.0 /255.0);


float drawLine(float uvValue, float y, float lowerEdge, float upperEdge) {
    return step(y - lowerEdge, uvValue) - step(y + upperEdge, uvValue);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Front Red Cross
    float frontRedCross = step(0.01, drawLine(uv.x, 0.5, 0.06, 0.06) + drawLine(uv.y, 0.5, 0.12, 0.12));

    // Front White Cross
    float frontWhiteCross = step(0.01, drawLine(uv.y, 0.5, 0.2, 0.2) + drawLine(uv.x, 0.5, 0.1, 0.1));
    float frontWhiteArea = frontWhiteCross - frontRedCross;

    // Back Red Cross
    float y = mix(0.0, 1.0, uv.x);
    float xCenter = step(0.5, uv.x);
    float backRedCross = step(0.01, drawLine(uv.y, y, 0.1 * (1.0 - xCenter), 0.1 * xCenter) + drawLine(uv.y, 1.0 - y, 0.1 * (1.0 - xCenter), 0.1 * xCenter));
    float backRedArea = backRedCross - frontWhiteArea - frontRedCross;

    // Back White cross
    float backWhiteCross = step(0.01, drawLine(uv.y, y, 0.15, 0.15) + drawLine(uv.y, 1.0 - y, 0.15, 0.15));
    float backWhiteArea = backWhiteCross - frontWhiteCross - backRedCross;

    // Background Blue Area
    float blueArea = (1.0 - backWhiteCross - frontWhiteCross);

    vec3 background = blueArea * blue + backWhiteArea * white + backRedArea * red;
    vec3 foreground = frontWhiteArea * white + frontRedCross * red;
    vec3 flag = max(background, foreground);

    // Output to screen
    fragColor = vec4(flag, 1.0);
}
