float samples = 50.;
float r = .5;
const vec3 c0 = vec3(0.9, 0.9, 0.9);
const vec3 c1 = vec3(1.0, 0.0, 0.0);
const vec3 c2 = vec3(1.0, 0.0, 1.0);

float rand(in vec2 uv) {
  return fract(sin(dot(uv, vec2(12.9898, 78.233) * 2.0)) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 res = vec2(iResolution.x / iResolution.y, 1);
  vec2 uv = gl_FragCoord.xy / iResolution.xy;
  uv.y = 1. - uv.y;
  uv = (uv - 0.5) * res + 0.5;
  float b = length(iMouse.xy / iResolution.xy - .5) * 10.;
  float edge = length(uv - 0.5) * 2.0;
  float col = 0.0;
  for (int i = 0; i < int(samples); i++) {
    float o = rand(uv + vec2(float(i), 0.0));
    o *= (clamp(uv.y - r * 0.5, 0.0, 1.0)) * max(0.0, b);
    col += smoothstep(0.01 + r + o, r, edge);
  }
  col /= samples;
  fragColor = vec4(mix(c0, mix(c1, c2, clamp(uv.y - r, 0.0, 1.0) * 2.0), col), 1.0);
}
