#define dt(p) dot(cos(p.zyx), .5-cos(p.xzy))
#define T iTime

vec3 a,b,c,m,n;

vec3 map01(float t) { return .25 + a*cos(t+m)*(b+c*cos(t+n));}

float map02(in vec3 p) {
    return -7.0*cos(dt(p.yzx) + .3) * sin(7.0*dt(p) + .7) + .5*sin(dt(p)+.1) * (.9+.5*cos(7.0*dt(p.xzy) + .1));
}

vec3 norm(in vec3 p) {
    vec3 m = map01(.05 * p.x + p.z);
    vec2 d = vec2(.06+.06*cos(p.x),0.);
    return map02(p)-vec3(
        map02(d.yxx-d.xyy),map02(m.xxy+m.yyx),map02(p.yxz)
    );
}
void mainImage( out vec4 fragColor, in vec2 coord ) {

    a = vec3(1.35,1.25,1.35) + 0.1*cos(5.0+0.7*iTime + vec3(0.5,1.0,2.0) );
    b = vec3(0.60,0.60,0.60) + 0.1*cos(4.0+0.5*iTime + vec3(2.5,5.0,3.0) );
    c = vec3(0.40,0.40,0.40) + 0.1*cos(1.0+0.3*iTime + vec3(6.0,2.0,4.2) );
    m = cos( 0.11*iTime + vec3(2.0,0.0,5.0) );
    n = cos( 0.17*iTime + vec3(3.0,1.0,4.0) );

    vec2 uv = coord/(iResolution.xy * .05);

    vec3 p = vec3(0.1,0.1,T/.1);
    p = vec3(uv.y, uv.x, map01(p.z * p.x * p.y).z);
    float d = map02(p.yzx);

    vec3 n = norm(p);
    float base = n.x+n.y;
    vec3 col = 0.5 + 0.5 * cos(.5 * map01(base) + T/.5 + vec3(0, 2, 4));

    fragColor = vec4(clamp(col,0.,1.),1.);
}
