void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x*=2.;

    vec4 o_info = f_o_info(iResolution, iTime);
    vec4 o_col = texture(iChannel1, uv);
    if(uv.x > 1.){
        o_col = texture(iChannel0, fract(uv));
    }
    if(uv.x < 1.){
        float n_d = abs(uv.y - o_info[0])*10.;
        vec4 o_add = vec4(1.,.0,.0,1.)*(pow(1.-n_d,3.));
        o_add = clamp(o_add, 0., 1.);
        o_col += o_add;
    }

    fragColor = o_col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec4 o_col_last = texture(iChannel0, uv);
    vec4 o_col_new = texture(iChannel1, uv);

    vec4 o_info = f_o_info(iResolution, iTime);

    float n_ratio = 1./iResolution.y;
    float n_d = abs(uv.y - o_info[0]);
    n_d = smoothstep(0., n_ratio*o_info[1], n_d);
    vec4 o_col = o_col_new;

    if(n_d == 1.){
        o_col = o_col_last;
    }
    fragColor = vec4(o_col);
}

vec4 f_o_info(vec3 o_scl_screen, float iTime)
{
    float n_fps = 60.;
    float n_pixels_per_row = 5.;
    float n_pixels_per_second = n_fps * n_pixels_per_row;
    float n_seconds = o_scl_screen.y / n_pixels_per_second;
    float n_y = fract(iTime*(1./n_seconds));
    return vec4(
        n_y, 
        n_pixels_per_row, 
        0.,
        0.
    );

}
