vec3 hsv2rgb(float h, float s, float v){   
    return clamp((abs(mod(vec3(0,2,1) / 3. + vec3(h),1.)
                          -.5)
                      +.5)
                  * 6. * s * v + v - 5. * s * v,
                 0.,1.);
}

void mainImage( out vec4 c, in vec2 coo )
{
    vec2 uv = coo / iResolution.y - vec2(iResolution.x/iResolution.y/2.,.5);

    float rad = atan(uv.y,uv.x) / 6.2832,
          dist = length(uv) * 2.;

    vec3 col = hsv2rgb(rad,dist,1.);

    col += smoothstep(0.99,1.,dist);

    c = vec4(col,1.);
}
