#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define pi 3.14159265359
vec4 hash44(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}
vec4 noise (vec2 U) {
    vec2 f = floor(U);
    vec2 c = ceil(U);
    vec4 _00 = pow(hash44(vec4(f,f)),vec4(1,3,3,3));
    vec4 _01 = pow(hash44(vec4(f.x,c.y,f.x,c.y)),vec4(1,3,3,3));
    vec4 _10 = pow(hash44(vec4(c.x,f.y,c.x,f.y)),vec4(1,3,3,3));
    vec4 _11 = pow(hash44(vec4(c,c)),vec4(1,3,3,3));
    vec4 _0 = mix(_00,_01,fract(U.y));
    vec4 _1 = mix(_10,_11,fract(U.y));
    return mix(_0,_1,fract(U.x));
}
vec4 fbm (vec2 U) {
    mat2 m = ei(.5);
    vec4 o = vec4(0);
    for (float i = 0.; i < 10.; i++) {
        U *= 1.5*m;
        o += noise(U)/5.*exp2(-.2*i);
    }
    return o;
}

Main {
    Q = B(U);
    vec4 q0 = A(U);
    vec4 h = hash44(vec4(U,iFrame,iTime));
    float a = 2.*pi*h.x;
    vec2 d = vec2(cos(a),sin(a));
    d += h.y;
    for (float i = 0.; i < 100.; i++) {
        vec4 q = A(U);
        float e = exp(-max(q.x,0.));
        U += 15.*d*e;
        vec4 h = hash44(vec4(U,iFrame,iTime));
        float a = 2.*pi*h.x;
        d = normalize(d+.5*(1.-e)*vec2(cos(a),sin(a)));
        Q += 30.*max(sin(.5+.5*atan(d.y,d.x)+.2*vec4(1,2,3,4)),0.)*exp(-.005*dot(U-vec2(0.5,1.1)*R,U-vec2(.5,1.1)*R))*(.2+q0.x);
    }

}
