// Function to get the audio sample
float getAudioSample(vec2 uv) {
    return texture(iChannel0, uv).r;
}

float hash(float n) { return fract(sin(n) * 43758.5453123); }

vec3 palette(float t) {
    return 0.25 + 1. * cos(6.28318 * (vec3(0.5, 0.5, 0.5) * t + vec3(0.0, 0.1, 0.2)));
}

mat2 rotate2D(float angle) {
    return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 mousePos = (vec2(iMouse) * 2.0 - iResolution.xy) / iResolution.y;

    // Get audio sample
    float audioSample = getAudioSample(vec2(0.0, uv.y * 0.5 + 0.5));

    // Initial distance calculation for UV manipulation
    float md = length(uv - mousePos);
    md -= 0.2;
    md = abs(md);
    md = 0.01 / md;

    // Apply initial transformations
    for (float i = 1.0; i < 7.0; i++) {
        uv = abs(uv);
        uv.x -= 1.2 * audioSample;
        uv *= 1.4 * (1.0 + 0.1 * audioSample);
        float angle = (iTime + (i * radians(45.0))) * 0.5;
        uv *= rotate2D(angle + (i * 0.2));
    }

    // Distance calculation for color modulation
    float d = length(uv);
    d -= 0.05;
    d = abs(d);
    d = 0.5 / d;

    // Base color using the palette function
    float t = iTime * 0.1 + audioSample * 10.0;
    vec3 col = palette(t + d) * 0.5 + palette(t * 0.7) * 0.5;

    // Sacred geometry and fractal morphing
    vec2 p = uv;
    for (int i = 0; i < 5; i++) {
        p = abs(p) / dot(p, p) - 0.5;
        p *= rotate2D(iTime * 0.1 + float(i) + audioSample * 3.14);
    }

    // Color modulation based on fractal geometry
    float f = length(p);
    col += palette(f * 0.5 + iTime * 0.2) * 0.5;

    // Final color output
    fragColor = vec4(col * d, 1.0);
}
