void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = 0.5 + 0.5*cos(vec3(0,2.0943951023931,4.1887902047863)-iTime);
    float angle = degrees(atan(fragCoord.x-iResolution.x/2.0,fragCoord.y-iResolution.y/2.0));
    col *= mod(angle-iTime*20.0/6.2831853071795864,20.0)<10.0?1.0:0.5;

    // Output to screen
    fragColor = vec4(col,1.0);
}
