void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = boxFilter(iChannel0, fragCoord, iResolution.xy, 8);
}

vec4 boxFilter(sampler2D tex, vec2 fragCoord, vec2 res, int hw)
{
    vec2 uv = fragCoord/res;

    vec4 color = vec4(0);
    for (int u = -hw; u <= hw; ++u) {
        for (int v = -hw; v <= hw; ++v) {
            color += texture(tex, (fragCoord + vec2(u,v)) / res); 
        }
    }
    color /= float((2*hw+1)*(2*hw+1));
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = boxFilter(iChannel0, fragCoord, iResolution.xy, 2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = boxFilter(iChannel0, fragCoord, iResolution.xy, 5);
}
