vec2 powi(vec2 number, float power){
    float angle = atan(number.y/number.x);
    float r = length(number);
    r = pow(r,power);
    return vec2(r*cos(angle*power),r*sin(angle*power));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord/iResolution.xy)*2.5-1.25;

    float scale = iResolution.x/iResolution.y;

    vec3 col;
    float x = scale*uv.x;
    float y = uv.y;
    vec2 pos = vec2(0.0,0.0);
    vec2 z = vec2(x,y);
    bool t = false;
    for(int i = 0; i < 256; i++){
        z = powi(z, 2.0) + vec2(1.0*sin(iTime/3.0),0.7*cos(iTime/3.0));
        if(length(z-vec2(x,y)) >= 5.0){
          col = vec3(pow(sin(float(i)/256.0),0.7),pow(sin(float(i)/256.0),0.7)*0.7+0.1,0.2);
          t = true;
          break;
        }
    }

    if(length(z-vec2(x,y)) < 5.0 && !t){
      col = vec3(0,0,0); 
    }

    // Output to screen

    fragColor = vec4(col,1.0);

}
