vec4 gausianblur(vec2 position) {
    // Gausian blur on image (stolen from existical on shadertoy)
    float Pi = 6.28318530718; // Pi*2

    // GAUSSIAN BLUR SETTINGS {{{
    float Directions = 16.0; // BLUR DIRECTIONS (Default 16.0 - More is better but slower)
    float Quality = 4.0; // BLUR QUALITY (Default 4.0 - More is better but slower)
    float Size = 3.0; // BLUR SIZE (Radius)
    // GAUSSIAN BLUR SETTINGS }}}

    vec2 Radius = Size/iResolution.xy;

    vec2 uv = position/iResolution.xy;

    // Pixel colour
    vec4 Color = texture(iChannel0, uv);

    // Blur calculations
    for( float d=0.0; d<Pi; d+=Pi/Directions)
    {
		for(float i=1.0/Quality; i<=1.0; i+=1.0/Quality)
        {
			Color += texture( iChannel0, uv+vec2(cos(d),sin(d))*Radius*i);		
        }
    }

    // Output to screen
    Color /= Quality * Directions - 15.0;

    return Color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 coord = vec2(iResolution.x - fragCoord.x, fragCoord.y);
    vec2 uv = fragCoord/iResolution.xy;
    vec2 Cuv = vec2(1.0 - uv.x, uv.y);

    // find normal of each pixel using the slope to each surrounding pixel and
    vec4 imcolour = gausianblur(coord);
    vec4 upcolour = gausianblur(coord + vec2(0, 1));
    vec4 dcolour  = gausianblur(coord + vec2(0, -1));
    vec4 lcolour  = gausianblur(coord + vec2(1, 0));
    vec4 rcolour  = gausianblur(coord + vec2(-1, 0));

    float lum = 0.2126*imcolour.x + 0.7152*imcolour.y + 0.0722*imcolour.z;
    float uplum = 0.2126*upcolour.x + 0.7152*upcolour.y + 0.0722*upcolour.z;
    float dlum = 0.2126*dcolour.x + 0.7152*dcolour.y + 0.0722*dcolour.z;
    float llum = 0.2126*lcolour.x + 0.7152*lcolour.y + 0.0722*lcolour.z;
    float rlum = 0.2126*rcolour.x + 0.7152*rcolour.y + 0.0722*rcolour.z;

    float height = 15.0;

    vec3 yslope = vec3(0, 1, height * (uplum - dlum));
    vec3 xslope = vec3(1, 0, height * (rlum - llum));

    vec3 normal = cross(yslope, xslope);

    normal = normalize(normal);


    // define a camera position and a light position, then calcuate the specular highlights
    // by finding the dot product of the 
    vec3 camera = vec3(0, 0, 100);
    vec3 light =  vec3((iMouse.xy - vec2(400, 225)),100);
    float brightness = 1.0;

    vec3 viewdir = vec3(0, 0, 1);

    vec3 camtoscreen = normalize(camera - vec3(fragCoord - vec2(400, 225), 0));
    vec3 lighttoscreen = normalize(light - vec3(fragCoord - vec2(400, 225), 0));

    vec3 reflected = reflect(-lighttoscreen, normal);

    vec3 spec = vec3(brightness * pow(max(dot(viewdir, reflected), 0.0), 16.0));

    float mousedif = length(fragCoord - iMouse.xy);

    //vec3 col = vec3(lum) * 0.2 + spec;
    vec3 col = imcolour.xyz * 0.2 + spec;


    // Output to screen
    fragColor = vec4(col,1.0);
}
