void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x / iResolution.y;
    // unstretch uvs
    uv.x *= ratio;

    float noiseScale = 0.15;

    // sample noise texture
    vec4 noise = texture(iChannel0, uv * noiseScale);


    float lineWidth = 0.0;
    float f = 0.5;
    float a = .72;   
    //float sinX = abs(cos((iTime + uv.x) * f) + sin((iTime + uv.y) * f)) * a;
    //float sinX = length(vec2(cos(uv.x + ratio), sin(uv.y))) * 0.5;
    float l = length(vec2(uv.x - ratio * .5, uv.y - .5));
    float r = smoothstep(.5, 0.8, pow(fract(pow(l, 2.0) * 2.0 - iTime * f), .5) * a);

    lineWidth += pow(r, 5.0);


    float c = .5 + sin(iTime * 1.0) * 0.05;
    //float b = step(c - lineWidth*.5, noise.x ) - step(c + lineWidth*.5, noise.x);
    float b = smoothstep(c - lineWidth * .9,c + lineWidth*.9, noise.x ) - step(c + lineWidth * .9, noise.x);

    //b = b > .5 ? float(c > noise.x - lineWidth) * float(c < noise.x + lineWidth) : b;

    float red = smoothstep(0.5, 1.0, b);

    b *= 2.0 * pow(r, .1);
    red *= .5;

    b -= pow(length(vec2(uv.x - ratio * .5, uv.y - .5)), 2.0) * 4.0;
    red -= pow(length(vec2(uv.x - ratio * .5, uv.y - .5)), 0.9) * .65;


    vec3 col = vec3(red + b *.1,0, b);


    fragColor = vec4(col,1.0);
}
