void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mv = iMouse.xy/iResolution.xy;

    // Texture pixel color
    vec4 texCol = texture(iChannel0, uv);

    //Directionally offset coordinate
    vec4 normCol = texture(iChannel0, vec2(uv.x - ((mv.x - uv.x) / (100.0)), uv.y - ((mv.y - uv.y) / (100.0))));
    for(int i = 0; i <= 1; i++)
    {



    }

    float normBright = 0.0;


    normBright = 0.0 + (texCol.r - normCol.r);


    normBright = (clamp(normBright * 5.0, -0.05, 1.0)/1.5);
    // Output to screen
    fragColor = vec4(texCol - normBright);
}
