void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float speed = 5.;
    float waveLength = 7.;
    float size = .04;
    float wave = speed * iTime + waveLength * uv.x;

    float offset = size*sin(wave);

    uv.y -= offset;

    float aspectRatio = iResolution.y/iResolution.x;

    uv -= vec2(0.5);
    uv = uv*vec2(1,aspectRatio);

    //Circle
    if(length(uv) < .15)
    {
        fragColor = vec4(1,0,0,0);
    }
    //backGround
    else
    {
        fragColor = vec4(1);
    }

    if(uv.y + .3 < size || uv.y > .3-size)
    {        
        fragColor = vec4(0,0,0,0);        
    }

    if(uv.x + .45 < size || uv.x > .45-size)
    {        
        fragColor = vec4(0,0,0,0);        
    }
    //Shadow
    if(sin(wave) < .0)
    {
        fragColor.xyz=fragColor.xyz*(1.0 + .5*sin(wave));
    }
}
