//Inspired by https://youtu.be/Myeatl2Q3wM
//go to the top of Buffer B to change mode
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 last = texture(iChannel0, uv);   
    vec4 curr = texture(iChannel1, uv);

	fragColor = curr.rgb == last.rgb ? vec4(curr.rgb, 1.) : vec4(curr.rgb, 0.);
}

#define mode 1
//pure diff = 0
//    color = 1
//      bnw = 2 (or anything else)
float random(vec2 seed) {
    return fract(sin(dot(seed, vec2(12.9898, 78.233))) * 43758.5453);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec4 curr = texture(iChannel0, uv);
    vec4 last = texture(iChannel1, uv);

    float diff = curr.a < 0.5 ? 1. : 0.;

    #if mode == 0
    fragColor = vec4(vec3(diff), 1.0);
    #elif mode == 1
    fragColor = iFrame!=0 && diff < .5 ? last : vec4(random(sin(fragCoord.xy + iTime)), random(sin(fragCoord.xy + iTime + 0.333)), random(sin(fragCoord.xy + iTime+ 0.667)), 1.0);
    #else
    fragColor = iFrame!=0 && diff < .5 ? last : vec4(vec3(random(fragCoord.xy + iTime)), 1.0);
    #endif
}
