void mainImage( out vec4 O, in vec2 n )
{    
    float a = iTime*0.01;    
    n = n/iResolution.xy * 2. - 1.0;
    for (int i = 0; i++<200;){
        n = (abs(n) - .5)*1.1*mat2(
            cos(a),-sin(a),
            sin(a),cos(a)
            );
    }
    O = vec4(length(n + vec2(-.7,.5)),length(n+vec2(-.2,.2)),length(n+vec2(.1,.9)),1.0);
}
