void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    //bool linhaPar = int(fragCoord.y)%2==0;

    int color = int(fragCoord.y)%3;

    float i = iTime;
    vec2 uv = fragCoord.xy / iResolution.xy;


    vec4 c = texture(iChannel0,uv);

    if(color==1){
        fragColor = vec4(c.r,0.,0.,0.);    
    }
    else
    if(color==2){
        fragColor = vec4(0.,c.g,0.,0.);    
    }
    else{
        fragColor = vec4(0.,0.,c.b,0.);    
    }


}
