#define PI 3.14159

vec2 lerp(vec2 a, vec2 b, float t)
{
    return (1.0 - t) * a + t * b;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float sliceAngle = 55.0;
    sliceAngle *= PI / 180.0;

    vec2 transformedUV = vec2(sin(sliceAngle), cos(sliceAngle)) * (uv - .5);
    float sliceMask = step(0.0, (transformedUV.x + transformedUV.y) / 2.0) * 2.0 - 1.0;

    float moveAmount = 0.1;
    vec2 moveValues = vec2(-cos(sliceAngle) * moveAmount, sin(sliceAngle) * moveAmount) * sliceMask;

    float timeScale = 1.0;
    float timeFrame = mod(iTime, timeScale) / timeScale;
    float timeFunc = 1.0 - pow(2.0, -10.0 * timeFrame);

    uv += lerp(vec2(0.0), moveValues, timeFunc);

    vec3 image = texture(iChannel0, uv).xyz;

    fragColor = vec4(image, 1.0);
}
