#define rot(x) mat2(cos(x+vec4(0,11,33,0)))

//Rodrigues-Euler axis angle rotation
#define ROT(p,axis,t) mix(axis*dot(p,axis),p,cos(t))+sin(t)*cross(p,axis)

//formula for creating colors;
#define H(h)  (  cos(  h*7. + 500.*vec3(1,2,3)   )*.6 + .5 )

//formula for mapping scale factor 
#define M(c)  log(1.+c)

#define R iResolution


void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0);
    vec4 rd = normalize( vec4(U-.5*R.xy, .5*R.y, R.y))*200.;

    float sc,dotp,totdist=0., tt=iTime/8., t=iTime;


    for (float i=0.; i<60.; i++) {

        vec4 p = vec4( rd*totdist)*20.;


        p.xyz += vec3(2000.*sin(tt),2000.*cos(tt),-6000); //20.*cos(tt)); 

        p.xz *= rot( .5 ) ;

        p.yzw = p.xyz; 

        sc = 1.; 

        p.zw *= rot( tt*2. );

        p.xw *= rot( tt );

        p.yw *= rot( tt/1.3);

        vec4 w = p;

        for (float j=0.; j<7.; j++) {

            p = abs(p);
            p = sqrt ( p.xyzw * p.yzwx ) *.8;

            dotp = max(1./dot(w,w),.03);
            sc *= dotp; 

            p = p * dotp - .4; 

            w = log(1.2+log(1.+log(1.+p)*p)*p) - .5; 


        }

        float dist = length(p)/sc ;

        float stepsize = dist/300. ;     
        totdist += stepsize;                  //move the distance along rd

        //accumulate color, fading with distance and iteration count

        if(i>20.)
        c +=
             .4 * 
             + H(M(sc)) * exp(-i*i*stepsize*stepsize*2e1);
    }

    c = 1. - exp(-c*c);
    O = ( vec4(c,0) );

}
