vec2 CenterUV(vec2 fragCoord )//uv的居中与反拉伸https://www.shadertoy.com/view/M3cSDS
{
    return (2.0 * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
}
vec2 RotateUV(vec2 uv,float angle)//uv旋转
{
    return vec2(uv.x * cos(angle) - uv.y * sin(angle),uv.x * sin(angle) + uv.y *cos(angle));
}
vec2 RotateUV(vec2 uv,float angle,vec2 point)
{
    uv -= CenterUV(point);
    uv = RotateUV(uv,(1.-clamp(step(1.,length(uv))*1.5,0.,1.))*iTime*2.);
    uv += CenterUV(point);
    return uv;
}
float CrateCheckerBoard(vec2 uv,float cellSize)//创建棋盘格https://www.shadertoy.com/view/lXdSzX
{
    vec2 checkeruv = step(mod(uv , cellSize * 2.), vec2(cellSize));
    return mix(checkeruv.x,1.-checkeruv.x,checkeruv.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = CenterUV(fragCoord );
    uv = RotateUV(uv,0.,iMouse.xy);
    float checker = CrateCheckerBoard(uv,0.2);


    // Output to screen
    fragColor = vec4(vec3(checker),1.0);
}
