vec2 CenterUV(vec2 fragCoord )//uv的居中与反拉伸https://www.shadertoy.com/view/M3cSDS
{
    return (2.0 * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
}

float CrateGrid(vec2 uv,float cellSize)//创建网格https://www.shadertoy.com/view/4fXyzr
{
    float line = 0.01;
    vec2 checkeruv = step(vec2(line),mod(uv,cellSize));
    return checkeruv.x* checkeruv.y;
}
float CrateCenterGrid(vec2 uv,float cellSize)//创建居中网格https://www.shadertoy.com/view/4fXyzr
{
    float line = 0.01;
    vec2 gridUV= step(line,abs(0.5 * cellSize- mod(uv - 0.5 * cellSize,cellSize)));
    return gridUV.x* gridUV.y;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = CenterUV(fragCoord );
    float mask = CrateCenterGrid(uv,0.2);

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col = mix(col,vec3(0.),mask);

    fragColor = vec4(col,1.0);
}
