// Map a floating point value to color
vec3 mapValueToColor(float value) {
   // Generate color using cosine function
   vec3 col = 0.5 + 0.2 * cos((value + vec3(0, 2, 4)));
   return col;
}

// Calculate the position of the light point
vec2 calculateLightPoint(vec2 pos, float time, float x) {
    float t = time * x;
    return pos+vec2(smoothstep(-0.75,0.75,sin(t))-0.5,smoothstep(-0.75,0.75,cos(t))-0.5);
}

// Flashing effect function
float flashing(float time) {
    return 0.6 + 0.2 * sin(2.0 * time) * sin(time);
}

// Lighting function
float lighting(vec2 fragCoord, vec2 position) {
    return 1.0 / abs(sqrt(distance(fragCoord, position) * 0.5));
}

// Render light points at a specific time
vec3 renderPointsAtTime(vec2 fragCoord, float currentTime) {
    vec3 color = vec3(0, 0, 0);
    vec2 position = vec2(0.5, 0.5);

    for (float i = 1.0; i < 12.0; i++) {
        float time = currentTime * (4.1 - (float(i) * 0.6));
        float lightingEffect = lighting(fragCoord, calculateLightPoint(position, time, i * 0.13)) * flashing(time);
        vec3 newColor = lightingEffect * mix(mapValueToColor(currentTime * (i + 1.0) / 2.0), vec3(0.02, 0.01, 0.02), 0.95);
        color = color + newColor;
    }
    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalize pixel coordinates to [0, 1]
    vec2 uv = fragCoord / iResolution.xy;

    // Final color value
    vec3 color = vec3(0, 0, 0);

    // Loop through time axis for rendering
    for (float i = 0.0; i < 1.0; i++) {
        float currentTime = iTime - i * 0.02;
        vec3 newColor = renderPointsAtTime(uv, currentTime) * ((10.0 - i) / 10.0);
        color = max(color, newColor);
    }

    // Output to the screen
    fragColor = vec4(color, 1.0);
}
