void SetCircle1(out vec2 c1, out float r1){
    r1 = 0.25;
    c1 = vec2(cos(iTime)*0.5+0.8,sin(iTime)*0.1+0.4);
}

void SetCircle2(out vec2 c2, out float r2){
    r2 = 0.2;
    c2 = vec2(sin(iTime+5.0)*0.5+0.8, cos(iTime+5.0)/5.+0.5);
}

void setBG(out vec3 BGColor){
    BGColor = vec3(0.2, 0.7 , 0.7);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 resolution = iResolution.xy;
    vec3 backgroundColor = tan(vec3(0.2,0.2,0.2));
    fragColor = vec4(backgroundColor, 1.0);
    vec2 tmp = fragCoord/iResolution.xy;
    vec2 c1, c2;
    float r1, r2;
    SetCircle1(c1, r1);
    SetCircle2(c2, r2);
    tmp.x *= iResolution.x/iResolution.y;
    if ((tmp.x - c1.x)*(tmp.x - c1.x) + (tmp.y - c1.y)*(tmp.y - c1.y) <= r1*r1){
        fragColor = vec4(0.6, 0.3, 0.3, 0.0);
    }
    if(tmp.y <= 1.1 - tmp.x && tmp.x>=0.2 && tmp.y >= 0.001 + tmp.x)
        fragColor = vec4(0.8, 0.1, 0.1, 0.0);
    if ((tmp.x - c2.x)*(tmp.x - c2.x) + (tmp.y - c2.y)*(tmp.y - c2.y) <= r2*r2){
        fragColor = vec4(0.6, 0.6, 0.3, 0.0);
    }
}
