const float PI = 3.1415926535;
const float TAU = 2. * PI;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / min(iResolution.x, iResolution.y);
    uv = uv * 2.5;

    vec3 bg = vec3(0.);

    vec3 colour = vec3(0.);
    float phi = mod(atan(uv.y, uv.x) / TAU + 1., 1.);
    float theta = atan(uv.y + uv.x / 3., uv.x);
    theta = theta / 4. + sin(cos(theta + iTime * 0.2) + iTime * 0.707) + iTime * 0.33;

    float sd = abs(length(uv + uv.yy * 0.04) - 1. + 0.1 * sin(uv.x + iTime * 0.2)) -  (0.021 + 0.03 * pow(cos(10. * theta), 2.));

    const vec3 A = vec3(1., 0., 0.);
    const vec3 B = vec3(1., 0.9, 0.);
    const vec3 X = vec3(0., 0., 1.);
    const vec3 Y = vec3(0., 0.9, 0.);

    if (phi < 0.25) {
        colour = mix(A, X, smoothstep(0.05, 0.20, phi));
    } else if (phi < 0.5) {
        colour = mix(X, Y, smoothstep(0.30, 0.45, phi));
    } else if (phi < 0.75) {
        colour = mix(Y, B, smoothstep(0.55, 0.70, phi));
    } else {
        colour = mix(B, A, smoothstep(0.80, 0.95, phi));
    }
    colour = mix(colour, vec3(1.), smoothstep(0., 0.11, abs(sd)));
    colour = colour * exp(-sd * 26.);
    fragColor = vec4(colour, 1.);
}
