#define S3(d,b) smoothstep(6./iResolution.y, 0. , d - (b) )
#define S2(d,b) smoothstep(6./iResolution.y, 0. , d - (b) )
#define S(d,b) smoothstep(6./iResolution.y, 0. , d - (b) )
#define PI     3.14159265
#define TWO_PI 6.28318530

float Sdf_I(float distA, float distB)
	{ return max(distA, distB);}
float Sdf_U(float distA, float distB)
	{ return min(distA, distB);}
float Sdf_D(float distA, float distB)
	{ return max(distA, -distB);}

vec3 DFB(vec3 pColObj, vec3 colOut, float distObj )
{ colOut = mix(colOut,pColObj ,S3( distObj,0.0));
  colOut = mix(colOut,vec3(0.0),S3(abs( distObj)-0.005,0.0));
  return colOut;
}

vec3 DFS(vec3 pColObj, vec3 colOut, float distObj )
{ colOut = mix(colOut,pColObj ,S( distObj,0.0)); return colOut;
  return colOut;
}



float sdBox( in vec2 p, in vec2 b )
{ vec2 d = abs(p)-b;  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);  }

float sdCircle( vec2 p, float r )
{ return length(p) - r;}



// Function to rotate a 2D point by an angle
vec2 rotate(vec2 p, float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return vec2(c * p.x - s * p.y, s * p.x + c * p.y);
}


